"use strict";function timer(r,e,t,s){clearInterval(r.timer1),clearInterval(r.timer2);var a=9;r.timer1=setInterval(function(){var e=new Date,t=new Date(s).getTime()-e.getTime();t=parseInt(t/1e3);var a=parseInt(t/60/60/24),c=parseInt(t/60/60%24);c=9<c?c:"0".concat(c);var n=parseInt(t/60%60);n=9<n?n:"0".concat(n);var i=t%60;i=9<i?i:"0"+i,$(r).html('<i class="day">'.concat(a,'</i>天<i class="hour">').concat(c,'</i>时<i class="minute">').concat(n,'</i>分<i class="second">').concat(i,'</i>秒 <i class="_second">1</i>'))},1e3),r.timer2=setInterval(function(){-1===a&&(a=9),$(r).children("._second").html(a),a--},100)}